import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailNhanVien = () => {
  const [nhanVien, setNhanVien] = useState({
    ma: "",
    ten: "",
    gioiTinh: "",
    diaChi: "",
  });
  const { id } = useParams();
  useEffect(() => {
    const fetchNhanVien = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/nhan-vien/detail/${id}`
        );
        setNhanVien(response.data.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchNhanVien();
  }, [id]);

  return (
    <div>
      <h1>Thông tin nhân viên</h1>
      <form>
        <div>
          <label>Mã:</label>
          <input type="text" value={nhanVien.ma} />
        </div>
        <div>
          <label>Tên:</label>
          <input type="text" value={nhanVien.ten} />
        </div>
        <div>
          <label>Giới tính:</label>
          <input type="text" value={nhanVien.gioiTinh} />
        </div>
        <div>
          <label>Địa chỉ:</label>
          <input type="text" value={nhanVien.diaChi} />
        </div>
      </form>
    </div>
  );
};

export default DetailNhanVien;
