import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DetailNhanVien = () => {
  const [nhanVien, setNhanVien] = useState({
    ma: "",
    ten: "",
    gioiTinh: "",
    diaChi: "",
  });
  const { id } = useParams();
  try {
    const respone = axios.put("http://localhost:8080/nhan-vien/detail/" + id);
    setNhanVien(respone.data.data);
    console.log(nhanVien);
  } catch (error) {
    console.error(error);
  }

  return (
    <div>
      <h1>Thông tin nhân viên</h1>
      <form>
        <div>
          <label>Mã:</label>
          <input type="text" value={nhanVien.ma} disabled />
        </div>
        <div>
          <label>Tên:</label>
          <input type="text" value={nhanVien.ten} disabled />
        </div>
        <div>
          <label>Giới tính:</label>
          <input type="text" value={nhanVien.gioiTinh} disabled />
        </div>
        <div>
          <label>Địa chỉ:</label>
          <input type="text" value={nhanVien.diaChi} disabled />
        </div>
      </form>
    </div>
  );
};

export default DetailNhanVien;
